import React, { useEffect, useState } from 'react';
import {
  StyleSheet,
  Text,
  View,
  SafeAreaView,
  TouchableOpacity,
  Linking,
  Image,
  TextInput,
  Keyboard,
} from 'react-native';
import { Button } from 'react-native-elements';
import Modal from 'react-native-modal';
import MapView, { Polyline, Marker } from 'react-native-maps';
import CustomButton from '../components/CustomButton';
import SpotifyIcon from '../svg/spotify-brands.svg';
import HomeIcon from '../svg/home-solid.svg';
import PaperPlane from '../svg/paper-plane-solid.svg';

const FinalScreen = ({
  navigation,
  runningRoute,
  playlist,
  setTotalKilometers,
}) => {
  const [isModalVisible, setModalVisible] = useState(false);
  const [inputValue, setInputValue] = useState('');

  return (
    <SafeAreaView style={styles.container}>
      <TouchableOpacity
        style={styles.homeButton}
        onPress={() => navigation.navigate('Home')}
      >
        <Text style={styles.homeButtonText}>Home </Text>
        <HomeIcon style={{ height: 20, width: 20 }} />
      </TouchableOpacity>
      <Text style={styles.headerText}>
        Enjoy your {runningRoute.km}km run in {runningRoute.routeName} route
      </Text>

      <View
        style={{
          flexDirection: 'row',
        }}
      >
        <Text style={{ textAlign: 'center' }}>
          How long have you run today?
        </Text>
        <TextInput
          style={{
            width: 50,
            height: 30,
            borderColor: 'gray',
            borderWidth: 1,
            borderRadius: 5,
          }}
          onChangeText={(number) => {
            setInputValue(number);
          }}
          value={inputValue}
          keyboardType="decimal-pad"
        />
        <Button
          buttonStyle={{
            backgroundColor: 'black',
            width: 50,
            borderRadius: 10,
          }}
          onPress={() => {
            setTotalKilometers((km) => {
              return (km = km + parseFloat(inputValue));
            });
            setInputValue('');
            Keyboard.dismiss();
          }}
          icon={<PaperPlane style={{ height: 15, width: 15 }} />}
        />
      </View>
      <View style={styles.result}>
        <View style={{ alignSelf: 'flex-start' }}>
          <TouchableOpacity
            style={styles.infoButton}
            onPress={() => {
              setModalVisible(true);
            }}
          >
            <Text style={styles.infoButtonText}>Info</Text>
          </TouchableOpacity>
        </View>

        <View>
          <MapView
            initialRegion={runningRoute.initialRegion}
            style={{ width: 300, height: 300, borderRadius: 5 }}
            provider={MapView.PROVIDER_GOOGLE}
          >
            <Polyline
              coordinates={runningRoute.coordinates}
              strokeWidth={3}
              strokeColor={'#0000FF'}
              geodesic={true}
            />
            <Marker coordinate={runningRoute.coordinates[0]} title={'start'} />
          </MapView>

          <TouchableOpacity
            style={styles.playlist}
            onPress={() => {
              Linking.openURL(playlist.url);
            }}
          >
            <Image
              style={{
                width: 90,
                height: 90,
                borderTopLeftRadius: 20,
                borderBottomLeftRadius: 20,
              }}
              source={playlist.img}
            />
            <SpotifyIcon style={{ height: 30, width: 30, marginBottom: 4 }} />
            <Text style={styles.playlistText}>playlist</Text>
          </TouchableOpacity>
        </View>
      </View>
      <Modal
        isVisible={isModalVisible}
        onSwipeComplete={() => setModalVisible(false)}
        swipeDirection="down"
      >
        <View style={styles.modalContainer}>
          <View style={styles.modalView}>
            <Text>{runningRoute.description}</Text>
          </View>
        </View>
      </Modal>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    alignItems: 'center',
    justifyContent: 'flex-start',
    flex: 1,
    margin: 10,
  },
  headerText: {
    height: 150,
    marginTop: 10,
    fontWeight: '800',
    fontSize: 40,
    textAlign: 'left',
    fontFamily: 'Geeza Pro',
    alignSelf: 'stretch',
    backgroundColor: 'green',
  },

  playlist: {
    width: 300,
    height: 90,
    flexDirection: 'row',
    marginTop: 15,
    backgroundColor: 'black',
    borderRadius: 20,
  },

  playlistText: {
    fontSize: 20,
    marginLeft: 5,
    fontFamily: 'Geeza Pro',
    color: 'red',
  },

  modalContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    borderRadius: 20,
  },

  modalView: {
    fontFamily: 'Geeza Pro',
    height: 400,
    width: 300,
    margin: 20,
    backgroundColor: 'white',
    borderRadius: 20,
    padding: 35,
    alignItems: 'center',
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 4,
    elevation: 5,
  },

  homeButton: {
    width: 100,
    height: 30,
    flexDirection: 'row',
    alignSelf: 'flex-end',
    backgroundColor: 'white',
    shadowColor: '#000',
    shadowOffset: { width: 2, height: 2 },
    shadowOpacity: 0.5,
    shadowRadius: 1,
    elevation: 3,
    borderRadius: 20,
    justifyContent: 'center',
    alignItems: 'center',
  },

  homeButtonText: {
    fontFamily: 'Geeza Pro',
    fontSize: 20,
    fontWeight: '600',
    marginTop: 3,
  },

  infoButton: {
    marginLeft: 25,
    marginBottom: 10,
    width: 65,
    height: 27,
    flexDirection: 'row',
    backgroundColor: 'white',
    shadowColor: '#000',
    shadowOffset: { width: 2, height: 2 },
    shadowOpacity: 0.3,
    shadowRadius: 0.5,
    elevation: 3,
    borderRadius: 10,
    justifyContent: 'center',
    alignItems: 'center',
  },

  infoButtonText: {
    fontFamily: 'Geeza Pro',
    fontSize: 18,
    marginTop: 3,
    color: 'black',
    fontWeight: '500',
  },

  result: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    width: '100%',
    backgroundColor: 'white',
    borderRadius: 20,
  },
});

export default FinalScreen;
